
SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;
SET time_zone = "+00:00";

DROP DATABASE IF EXISTS `alvas`;
CREATE DATABASE `alvas`;
USE `alvas`;


CREATE TABLE `alvas` (
  `id` int(11) NOT NULL,
  `diakid` int(11) DEFAULT NULL,
  `datum` date DEFAULT NULL,
  `lefekves` time DEFAULT NULL,
  `felkeles` time DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


CREATE TABLE `diak` (
  `id` int(11) NOT NULL,
  `nev` varchar(255) DEFAULT NULL,
  `fiu` bit(11) DEFAULT NULL,
  `szuldatum` date DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


CREATE TABLE `naptar` (
  `datum` date NOT NULL,
  `melynap` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;








ALTER TABLE `alvas`
  ADD PRIMARY KEY (`id`);


ALTER TABLE `diak`
  ADD PRIMARY KEY (`id`);


ALTER TABLE `naptar`
  ADD PRIMARY KEY (`datum`);
COMMIT;
